import i18n from "i18next";
import LanguageDetector from "i18next-browser-languagedetector";
import { initReactI18next } from "react-i18next";
import translationAZ from "./json/translateAz.json";
import translationEN from "./json/translateEN.json";
import translationRU from "./json/translateRU.json";



i18n
  .use(LanguageDetector)
  .use(initReactI18next) 
  .init({
    // we init with resources
    lng: "aze",  // default language
    resources: {
      aze: {
        translations: translationAZ,
        "faqcontent2":<>Bizim cəmiyyətimizin üzvləri bir-biri ilə fikir alış verişləri də Cəmiyyətimizə üzv ola bilərlər.  <br /> <br /> Üzvlərimizi seçərkən nəzərə aldığımız nüanslardan biri də sahə mütəxəssisləri və digər adını çəkdiyimiz şəxslərin kollektiv düşüncə tərzimizə uyğun olmasıdır. Çünki, bu sahələr üzrə kifayət qədər biliyə malik və özünü inkişaf etdirmiş insanlar digər üzvlərə də əhəmiyyətli töhfə vermək qabiliyyətinə malikdirlər və beləliklə diskussiyalar daha dərin və mənalı olur.</>,

      },
      en: {
        translations: translationEN
      },
      rus: {
        translations: translationRU
      }
    },
    fallbackLng: false,
    debug: true,

    // have a common namespace used around the full app
    ns: ["translations"],
    defaultNS: "translations",

    keySeparator: false, // we use content as keys

    interpolation: {
      escapeValue: false
    }
  });

export default i18n;
