import i18n from "i18next";
import LanguageDetector from "i18next-browser-languagedetector";
import { initReactI18next } from "react-i18next";

i18n
  .use(LanguageDetector)
  .use(initReactI18next)
  .init({
    // we init with resources
    resources: {
      aze: {
        translations: {
          "Section1":
            "Fikirlər yalnız fikir olaraq heç nəyi dəyişməyə qadir deyil. Hacklab fikirlərinizin dünyanı dəyişə biləcəyi və gələcəyin qurulmasında rol ala biləcəyiniz texnoloji reallığa açılan qapı olacaq.",
          "Welcome to React": "Welcome to React and react-i18next",
          welcome: "Hello <br/> <strong>World</strong>",
          ab: "Fikirlər yalnız fikir olaraq heç nəyi dəyişməyə qadir deyil. Hacklab fikirlərinizin dünyanı dəyişə biləcəyi və gələcəyin qurulmasında rol ala biləcəyiniz texnoloji reallığa açılan qapı olacaq."
        }
      },
      en: {
        translations: {
          "Section1":
            "Thoughts alone cannot change anything. A technology-opening door where you can explore the world of your hacklab and take part in its future construction.",
          "Welcome to React": "Willkommen bei React und react-i18next",
          welcome: "Hallo Welt",
          ab: "Thoughts alone cannot change anything. A technology-opening door where you can explore the world of your hacklab and take part in its future construction."
        }
      },
      rus: {
        translations: {
          "Section1":
            "Одни мысли ничего не могут изменить. Возможность исследовать мир и роль вашего взломанного объекта в строительстве будущего - это технологически открытая дверь.",
          "Welcome to React": "Willkommen bei React und react-i18next",
          welcome: "Hallo Welt",
          ab: "Thoughts alone cannot change anything. A technology-opening door where you can explore the world of your hacklab and take part in its future construction."
        }
      }
    },
    fallbackLng: "aze",
    debug: true,

    // have a common namespace used around the full app
    ns: ["translations"],
    defaultNS: "translations",

    keySeparator: false, // we use content as keys

    interpolation: {
      escapeValue: false
    }
  });

export default i18n;
