import "./Navbar.scss";
import loqo from "../assets/loqob.png";
import times from "../assets/cancel.png";
import earth from "../assets/language.png";
import earth1 from "../assets/language1.png";
import { NavLink, withRouter } from "react-router-dom";
import $ from "jquery";
import { connect } from "react-redux";
import React, { useState, useEffect } from "react";
import { useTranslation, Trans } from "react-i18next";
import { Wallet } from "./SolanaConnect";

function Navbar({ displaymode }) {
 

  useEffect(() => {
    $(function () {
      $(".fa-bars").click(function () {
        $(".navmenu").css({ visibility: "visible", right: "0%" });
      });
    });
    $(".fa-times").click(function () {
      $(".navmenu").css({ visibility: "hidden", right: "-60%" });
    });

  });

  const { t, i18n } = useTranslation();

  const changeLanguage = (lng) => {
  console.log("clicked")
    i18n.changeLanguage(lng);
  };
  return (
    <div id="up" className="navbar" style={{ position: displaymode }}>
      <NavLink to="/">
        <img src={loqo} alt="" />
      </NavLink>
      <ul className="navmenu">
        <li>
          <img src={times} className="fa-times" alt="" />
        </li>
         <li>
          <NavLink className="first-li" to="/fond">
               Fond
          </NavLink>
        </li>
         <li>
          <NavLink className="first-li" to="/cemiyyet">
               Cəmiyyət
          </NavLink>
        </li>
        <li>
          <NavLink className="first-li" to="/meeting">
                Tədbirlər
          </NavLink>
        </li>
         <li>
          <NavLink className="second-li" to="/faq">
              FAQ
          </NavLink>
        </li>
        <li>
        <NavLink className="third-li" to="/blog">
              Blog 
          </NavLink>
        </li>
       
        
        {/*<li className="last-li-one">
          <Wallet />
        </li>*/}
       
        {/*< <li>
        <div class="dropdown">
     <img src={earth1}  role="button" id="dropdownMenuLink" data-toggle="dropdown" aria-haspopup="true" aria-expanded="false" />
        <div class="dropdown-menu" aria-labelledby="dropdownMenuLink">
        <button id="dropbutton" class="dropdown-item" style={{zIndex:"1000"}} onClick={() => changeLanguage("aze")}>aze</button>
        <button id="dropbutton"  class="dropdown-item" style={{zIndex:"1000"}} onClick={() => changeLanguage("en")}>en</button>
        <button id="dropbutton"  class="dropdown-item"   style={{zIndex:"1000"}} onClick={() => changeLanguage("rus")}>rus</button>
        </div> 
     </div> 
        </li> */}
          <li>
              <select id="lang">
  <option value="1" >AZ</option>
  <option value="2" >EN</option>
  <option value="3" >RU</option>
      </select>
        </li>
  

      </ul>
     
      <div class="burger">
        <i class="fas fa-bars"></i>
      </div>
    </div>
  );
}
const mapStateToProps = (state, ownProps) => {
  return {
    displaymode: state.displaymode
  };
};

export default connect(mapStateToProps)(Navbar);
