import "./Section2.scss";
import React, { useState, useEffect } from "react";
import { connect } from "react-redux";
import Marquee from "react-fast-marquee";
import gif2 from "../assets/dalga.gif";
import { Trans,Translation } from "react-i18next";
import i18n from '../i18n';


function Section2({ text1 = [] }) {
 

  const [s, setS] = useState();
  return (
    <div className="Section2">
      <div class="gif">
        <img
          className="gifimg"
          src={gif2}
          alt=""
        />
        <Marquee
          speed="120"
          direction="left"
          gradientColor
          style={{
            height: "clamp(60px, 10.10vw, 190px)",
            fontSize: "clamp(16px, 6.78vw, 130px)",
            padding: "0px",
          
          }}
        >
          HACKLAB HACKLAB HACKLAB HACKLAB HACKLAB HACKLAB HACKLAB HACKLAB
          HACKLAB HACKLAB HACKLAB HACKLAB HACKLAB HACKLAB HACKLAB HACKLAB
          HACKLAB HACKLAB HACKLAB HACKLAB HACKLAB 
          HACKLAB HACKLAB HACKLAB HACKLAB HACKLAB HACKLAB HACKLAB HACKLAB
          HACKLAB HACKLAB HACKLAB HACKLAB HACKLAB HACKLAB HACKLAB HACKLAB
          HACKLAB HACKLAB HACKLAB HACKLAB HACKLAB
        </Marquee>
        <div className="divs1">
          {text1.map((tex) => {
            return (
              <div className="div1" id="#div1">
                <img src={tex.image} alt="" />
                <div className="title">
                 <Translation i18n={i18n} >
                 {
                     (t, {i18n}) => <span>{t(tex.title)}</span>
                 }
                 </Translation>
                </div>
                <div className="content">
                 <Translation i18n={i18n} >
                 {
                     (t, {i18n}) => <span>{t(tex.content)}</span>
                 }
                 </Translation>
                </div>
              </div>
            );
          })}
        </div>
      </div>
    </div>
  );
}

const mapStateToProps = (state, ownProps) => {
  return {
    text1: state.text1
  };
};

export default connect(mapStateToProps)(Section2);
