import i18n from "i18next";
import LanguageDetector from "i18next-browser-languagedetector";
import { initReactI18next } from "react-i18next";

i18n
  .use(LanguageDetector)
  .use(initReactI18next)
  .init({
    // we init with resources
    resources: {
      aze: {
        translations: {
          "Section1":
            "Fikirlər yalnız fikir olaraq heç nəyi dəyişməyə qadir deyil. Hacklab fikirlərinizin dünyanı dəyişə biləcəyi və gələcəyin qurulmasında rol ala biləcəyiniz texnoloji reallığa açılan qapı olacaq.",
          title1: "Ekspertlər",
          content1:
            "Cəmiyyət üzvləri bir çox texnoloji sahədə çalışmış və çalışmağa davam edən sahə mütəxəssislərindən ibarətdir.",
          title2: "Sənaye",
          content2:
            "Fəaliyyətimizin prioritet istiqamətləri robotexnika, dana analizi, IoT, blokçeyn və digər Sənayə 4.0 sahələridir. ",
          title3: "Antiinkubator",
          content3:
            "Qoşulmaq üçün, şirkət açmağa və hər hansı bir müddətdə maliyyələşdirməni gözləməyə ehtiyyac yoxdur.",
          title4: "İnvestisiya",
          content4:
            "Cəmiyyətə üzv olaraq, layihələriniz üçün cəmiyyətin investisiya imkanlarından yararlanma şansı əldə edirsiniz."
        }
      },
      en: {
        translations: {
          "Section1":
            "Thoughts alone cannot change anything. A technology-opening door where you can explore the world of your hacklab and take part in its future construction.",
          title1: "Experts",
          content1:
            "The members of the society are specialists in the field who have worked and continue to work in many technological fields.",
          title2: "Industry",
          content2:
            "The priority areas of our activity are robotics, calf analysis, IoT, blockchain and other areas of Industry 4.0.",
          title3: "Antiincubator",
          content3:
            "To join, you do not need to open a company and wait for funding at any time.",
          title4: "Investment",
          content4:
            "As a member of the society, you get a chance to take advantage of the company's investment opportunities for your projects."
        }
      },
      rus: {
        translations: {
          "Section1":
            "Одни мысли ничего не могут изменить. Возможность исследовать мир и роль вашего взломанного объекта в строительстве будущего - это технологически открытая дверь.",
          welcome: "Hallo Welt",
        }
      }
    },
    fallbackLng: "aze",
    debug: true,

    // have a common namespace used around the full app
    ns: ["translations"],
    defaultNS: "translations",

    keySeparator: false, // we use content as keys

    interpolation: {
      escapeValue: false
    }
  });

export default i18n;
