const RootReducer = (state = initialState, action) => {
  console.log("action.id " +  action.id)
  console.log("state.search " + state.search)
  switch (action.type) {
    case "DELETE_CARD":
      return {
        ...state,
      search: String(action.id)
      };   
      case "AFTER_SEARCH":
      let s = state.text2.filter(
        (text2) =>  text2.title.toLowerCase().includes(action.id.toLowerCase())
      );
       let s3 = state.text2.filter(
        (text2) =>  toString(text2.content.Empty).toLowerCase().includes(action.id.toLowerCase())
      );
      let s0 = state.text3.filter(
        (text2) =>  text2.title.toLowerCase().includes(action.id.toLowerCase())
      );
      let s1 = state.text4.filter(
        (text2) =>  text2.title.toLowerCase().includes(action.id.toLowerCase())
      );
      let s2 = state.text5.filter(
        (text2) =>  text2.title.toLowerCase().includes(action.id.toLowerCase())
      );
      let s4 = state.section1text.filter(
        (text2) =>  text2.toLowerCase().includes(action.id.toLowerCase())
      );

      let alls = s.concat(s1,s2,s0,s4)



      return {
        ...state,
        search: alls


      };
    default:
      return state;
  }
};

export default RootReducer;
