import "./Section2.scss";
import React, { useState, useEffect } from "react";
import { connect } from "react-redux";
import Marquee from "react-fast-marquee";
import gif2 from "../assets/dalga.gif";
import { Trans,Translation } from "react-i18next";
import i18n from '../i18n';


function Section2({ text1 = [] }) {
 const CardFuncEnter = () => {
    document.querySelector(".section2div1").style.transform = "rotateY(180deg)";
    document.querySelector(".section2div1").style.transition = "all 1s ease-in-out";
    setTimeout(() => {
      document.querySelector(".section2img").style.visibility = "hidden";
      document.querySelector(".section2title").style.visibility = "hidden";
      document.querySelector(".section2content").style.visibility = "visible";
    }, 1000);
  };
  const CardFunc = () => {
    document.querySelector(".section2div1").style.transform = " rotateY(0deg)";
    document.querySelector(".section2div1").style.transition = "all 1s ease-in-out";
    setTimeout(() => {
      document.querySelector(".section2img").style.visibility = "visible";
      document.querySelector(".section2title").style.visibility = "visible";
      document.querySelector(".section2content").style.visibility = "hidden";

    }, 1000);
  };
 

  const [s, setS] = useState();
  return (
    <div className="Section2">
     

        <div className="divs1">
          {text1.map((tex) => {
            return (
              <div className="section2div1div1" id="#div1"  onMouseEnter={CardFuncEnter} onMouseLeave={CardFunc} >
                <img src={tex.image} alt="" id="section2img" />
                <div className="title" id="section2title">
                 <Translation i18n={i18n} >
                 {
                     (t, {i18n}) => <span>{t(tex.title)}</span>
                 }
                 </Translation>
                </div>
                <div className="content" id="section2content">
                 <Translation i18n={i18n} >
                 {
                     (t, {i18n}) => <span>{t(tex.content)}</span>
                 }
                 </Translation>
                </div> 
              </div>
            );
          })}
        </div>
   
    </div>
  );
}

const mapStateToProps = (state, ownProps) => {
  return {
    text1: state.text1
  };
};

export default connect(mapStateToProps)(Section2);
