import React, { Component , useState ,useRef,useEffect } from "react";
import image from "../assets/loqow.png";
import "./Footer.scss";
import $ from 'jquery';
import { NavLink, withRouter } from "react-router-dom";
import { connect } from "react-redux";
const Footer = ({deletecard}) => {

 function searchAndHighlight(searchTerm, selector) {
            if (searchTerm) {               
                var selector = selector || "#realTimeContents"; //use body as selector if none provided
                var searchTermRegEx = new RegExp(searchTerm, "ig");
                var matches = $(selector).text().match(searchTermRegEx);
                if (matches != null && matches.length > 0) {
                    $('.highlighted').removeClass('highlighted'); //Remove old search highlights 
 
                    //Remove the previous matches
                    $span = $('#realTimeContents span');
                    $span.replaceWith($span.html());
 
                    if (searchTerm === "&") {
                        searchTerm = "&amp;";
                        searchTermRegEx = new RegExp(searchTerm, "ig");
                    }
                    $(selector).html($(selector).html().replace(searchTermRegEx, "<span class='match'>" + searchTerm + "</span>"));
                    $('.match:first').addClass('highlighted');
 
                    var i = 0;
 
                    $('.next_h').off('click').on('click', function () {
                        i++;
 
                        if (i >= $('.match').length) i = 0;
 
                        $('.match').removeClass('highlighted');
                        $('.match').eq(i).addClass('highlighted');
                        $('.ui-mobile-viewport').animate({
                            scrollTop: $('.match').eq(i).offset().top
                        }, 300);
                    });
                    $('.previous_h').off('click').on('click', function () {
 
                        i--;
 
                        if (i < 0) i = $('.match').length - 1;
 
                        $('.match').removeClass('highlighted');
                        $('.match').eq(i).addClass('highlighted');
                        $('.ui-mobile-viewport').animate({
                            scrollTop: $('.match').eq(i).offset().top
                        }, 300);
                    });
 
 
 
 
                    if ($('.highlighted:first').length) { //if match found, scroll to where the first one appears
                        $(window).scrollTop($('.highlighted:first').position().top);
                    }
                    return true;
                }
            }
            return false;
        }
 
        $(document).on('click', '.searchButtonClickText_h', function (event) {
 
            $(".highlighted").removeClass("highlighted").removeClass("match");
            if (!searchAndHighlight($('.textSearchvalue_h').val())) {
                alert("No results found");
            }
 
 
        });

  const [search, setSearch] = useState("");
  
  const handleChange = (e) => {
    setSearch(e.target.value);
  };

  const handleClick = (e) => {
    deletecard(search);
  };





    return (
      <div className="footer">
        <div className="main">
          <div className="child-one">
            <img src={image} />
            <p>Tel: +994 12 430 49 93</p>
            <p className="line-more">Mail: info@hacklab.az</p>
            <p className="line-less">
              Ünvan: Bakı şəhəri, Atatürk pr. 30A
            </p>
            <span className="right">2021. Bütün hüquqlar qorunur.</span>
          </div>
          <div className="child-two">
            <p className="facebook">
              <a href="https://www.facebook.com/hacklabaz">Facebook</a>
            </p>
            <p className="instagram">
              <a href="https://www.instagram.com/hacklabaz/">Instagram</a>
            </p>
            <p className="linkedin">
              <a href="https://www.linkedin.com/company/hacklabaz">Linkedin</a>
            </p>
          </div>
          <div className="child-three">
            <div className="two-two">
              <a href="/" className="home-hov">Ana səhifə</a>
              <a href="#up" className="up">
                Yuxarı qayıt
              </a>
            </div>
       
            <p>
              <a href="/faq" className="faq-hover">FAQ</a>
            </p>
            <div className="search-icon">
              <input name="text-12" id="text-12" type="text" onChange={handleChange}   class="textSearchvalue_h" placeholder="Axtar" />
              <div class="fa-search1"  onClick={handleClick}>
                <img src="https://i.ibb.co/Yjc7Q3K/Whats-App-Image-2021-10-29-at-14-25-33-removebg-preview.png" class="searchButtonClickText_h" />
              </div>
            </div>
            <div className="end">site by: ZOOM Agency</div>
          </div>
        </div>
      </div>
    );
  
}

const mapDispatchToProps = (dispatch) => {
  return {
    deletecard: (search) => {
      dispatch({ type: "DELETE_CARD", id: search });
    }
  };
};



export default connect(null, mapDispatchToProps)(Footer)
