import "./Navbar.scss";
import loqo from "../assets/loqob.png";
import times from "../assets/cancel.png";
import earth from "../assets/language.png";
import earth1 from "../assets/language1.png";
import { NavLink, withRouter } from "react-router-dom";
import $ from "jquery";
import { connect } from "react-redux";
import React, { useState, useEffect } from "react";
import { useTranslation, Trans } from "react-i18next";
import { Wallet } from "./SolanaConnect";

function Navbar({ displaymode }) {
 

  useEffect(() => {
    $(function () {
      $(".fa-bars").click(function () {
        $(".navmenu").css({ visibility: "visible", right: "0%" });
      });
    });
    $(".fa-times").click(function () {
      $(".navmenu").css({ visibility: "hidden", right: "-60%" });
    });

    //------change language function
    useEffect(() => {
    $(".dropdown-item1").css("display", "none");
  }, []);

  useEffect(() => {
    $("button").html("aze");
    $(".dropdown-item1").click(() => {
      $("button").html("aze");
      $(".dropdown-item1").css("display", "none");
      $(".dropdown-item2").css("display", "block");
      $(".dropdown-item3").css("display", "block");
      changeLanguage("aze")
    });
    $(".dropdown-item2").click(() => {
    changeLanguage("en")
      $("button").html("en");
      $(".dropdown-item2").css("display", "none");
      $(".dropdown-item1").css("display", "block");
      $(".dropdown-item3").css("display", "block");
    });
    $(".dropdown-item3").click(() => {
     changeLanguage("rus")
      $("button").html("rus");
      $(".dropdown-item3").css("display", "none");
      $(".dropdown-item1").css("display", "block");
      $(".dropdown-item2").css("display", "block");
    });
  });
     $('#lang').change(function() {
        if ($(this).val() == "1") {
        changeLanguage("aze")
        $("#option1").attr('hidden', '');
        $("#option2").removeAttr('hidden');
        $("#option3").removeAttr('hidden');
       
      } 
       else if ($(this).val() == "2") {
       changeLanguage("en")
       $("#option2").attr('hidden', '');
       $("#option1").removeAttr('hidden');
       $("#option3").removeAttr('hidden');
      
      }
       else if ($(this).val() == "3") {
       changeLanguage("rus")
       $("#option3").attr('hidden', '');
       $("#option2").removeAttr('hidden');
       $("#option1").removeAttr('hidden');
     
      }
    }); 

  });

  const { t, i18n } = useTranslation();

  const changeLanguage = (lng) => {
  console.log("clicked")
    i18n.changeLanguage(lng);
  };
  return (
    <div id="up" className="navbar" style={{ position: displaymode }}>
      <NavLink to="/">
        <img src={loqo} alt="" />
      </NavLink>
      <ul className="navmenu">
        <li>
          <img src={times} className="fa-times" alt="" />
        </li>
         <li>
          <NavLink to="/fond">
            <div className="tuc">Fond</div>
          </NavLink>
        </li>
         <li>
          <NavLink to="/cemiyyet">
            <div className="tuc">Cəmiyyət</div>
          </NavLink>
        </li>

        <li>
          <NavLink to="/meeting">
            <div className="tuc">Tədbirlər</div>
          </NavLink>
        </li>
         <li>
          <NavLink to="/faq">
            <div className="tuc">FAQ</div>
          </NavLink>
        </li>
         <li>
          <NavLink to="/blog">
            <div className="tuc">Blog</div>
          </NavLink>
        </li>        
        {/*<li className="last-li-one">
          <Wallet />
        </li>*/}
          <li>
              <select id="lang">
        <option id="option1" value="1" >AZ</option>
        <option id="option2" value="2" >EN</option>
        <option id="option3" value="3" >RU</option>
             </select>
        </li>
  

      </ul>
     
      <div class="burger">
        <i class="fas fa-bars"></i>
      </div>
    </div>
  );
}
const mapStateToProps = (state, ownProps) => {
  return {
    displaymode: state.displaymode
  };
};

export default connect(mapStateToProps)(Navbar);
