import i18n from "i18next";
import LanguageDetector from "i18next-browser-languagedetector";
import { initReactI18next } from "react-i18next";
import translationAZ from "./json/translateAz.json";
import translationEN from "./json/translateEN.json";
import translationRU from "./json/translateRU.json";



i18n
  .use(LanguageDetector)
  .use(initReactI18next) 
  .init({
    // we init with resources
    lng: "aze",  // default language
    resources: {
      aze: {
        translations: translationAZ,
        "faqcontent2":"Bizim cəmiyyətimizin üzvləri bir-biri ilə fikir alış verişi edən, yeniliyə can atan, daim yeni texnologiya dünyası  ilə maraqlı , öz şirkətini qurmağa həvəsli sahibkarlar və ya yeni sahələrə xüsusi marağı olan , innovativ və kreativ fikirlərlə dolu , texnoloji sahədə sonsuz inkişaf etmək istəyən sahə mütəxəssisləridir. Onlara Proqram mühəndisləri, Texniki təcrübəyə malik dizaynerlər, Məhsul Menecerləri və ya Marketinq Mütəxəssisləri, Texniki təşəbbüskarlar aiddir. Texniki və texnoloji sahələrdən əlavə texniki keçmişə sahib olan və ya texniki sahələrə keçən - idmançılar, müəllimlər, cəmiyyət idarəçiləri və s. kimi spesifik sahə mütəxəssisləri də Cəmiyyətimizə üzv ola bilərlər.  <br \/> <br \/> Üzvlərimizi seçərkən nəzərə aldığımız nüanslardan biri də sahə mütəxəssisləri və digər adını çəkdiyimiz şəxslərin kollektiv düşüncə tərzimizə uyğun olmasıdır. Çünki, bu sahələr üzrə kifayət qədər biliyə malik və özünü inkişaf etdirmiş insanlar digər üzvlərə də əhəmiyyətli töhfə vermək qabiliyyətinə malikdirlər və beləliklə diskussiyalar daha dərin və mənalı olur.",

      },
      en: {
        translations: translationEN
      },
      rus: {
        translations: translationRU
      }
    },
    fallbackLng: false,
    debug: true,

    // have a common namespace used around the full app
    ns: ["translations"],
    defaultNS: "translations",

    keySeparator: false, // we use content as keys

    interpolation: {
      escapeValue: false
    }
  });

export default i18n;
