import "./Section2.scss";
import React, { useState, useEffect } from "react";
import { connect } from "react-redux";
import Marquee from "react-fast-marquee";
import gif2 from "../assets/dalga.gif";
import { Trans,Translation } from "react-i18next";
import i18n from '../i18n';


function Section2({ text1 = [] }) {
 useEffect(() => {
       
        $(`.Section2 .div1`).mouseenter(function () {
        $(".Section2 .div1").css({ transform: "rotateY(180deg)" });
        $(".Section2 .div1").css("transition", "all 1s ease-in-out");
       setTimeout(() => {
       $(".Section2 img").css("visibility", "hidden");
       $(".Section2 .title").css("visibility", "hidden");
       $(".Section2 .content").css("visibility", "visible");
       }, 1000);
       });

         $(`.Section2 .div1`).mouseleave(function () {
  
       });
    
  });
 
  const CardFunc = () => {
    $(".Section2 .div1").css({ transform: "rotateY(0deg)" });
    $(".Section2 .div1").css("transition", "all 1s ease-in-out");
    setTimeout(() => {
      $(".Section2 img").css("visibility", "visible");
      $(".Section2 .title").css("visibility", "visible");
      $(".Section2 .content").css("visibility", "hidden");
    }, 1000);
  };
 

  const [s, setS] = useState();
  return (
    <div className="Section2">
     

        <div className="divs1">
          {text1.map((tex) => {
            return (
              <div className="div1" >
                <img src={tex.image} alt="" />
                <div className="title">
                 <Translation i18n={i18n} >
                 {
                     (t, {i18n}) => <span>{t(tex.title)}</span>
                 }
                 </Translation>
                </div>
                <div className="content">
                 <Translation i18n={i18n} >
                 {
                     (t, {i18n}) => <span>{t(tex.content)}</span>
                 }
                 </Translation>
                </div>
              </div>
            );
          })}
        </div>
   
    </div>
  );
}

const mapStateToProps = (state, ownProps) => {
  return {
    text1: state.text1
  };
};

export default connect(mapStateToProps)(Section2);
